const fs = require('fs')
const path = require('path')
let commandsArray = []


function loadCMDS(dir, handler) {
    fs.readdir(`${require.main.path}/${dir}`, (err, files) => {
        let totalCommands = 0
        for (const cmd in files) {
            totalCommands++
        }
        console.log(`Loaded a total of ${totalCommands} commands.`)
        for (const cmd of files.filter(x => x.endsWith('.js'))) {
            const File = require(`${require.main.path}/${dir}/${cmd}`)
            if (handler.showLoadedCMDS === true) {
            if (isReady(File)) {
                console.log(`Stop Prefab => loaded command "${File.name}"`)
                commandsArray.push(File.name)
                handler.commands.set(File.name, File)

            
                let emojis = require('../presets/categories')
                let emojiMatch = emojis[File.category]
                handler.categories.set(File.category, emojiMatch)

                
            } else {
                console.log(`Stop Prefab => Failed to load command "${cmd.replace('.js', '')}"`)
            }
        }
    }
        
        
  })
function isReady(file) {
    if (!file.execute || !file.name || !file || file === {}) {
        return false
    } else {
        return true
    }
}
}

module.exports.load = loadCMDS
module.exports.array = commandsArray
